sum by(instance,name) (
    label_replace(rate(container_cpu_usage_seconds_total{name=~".+"}[$__rate_interval]), "instance", "cadvisor", "instance", ".*cadvisor.*")
) / on (instace) group_left() sum by (instance) (
    label_replace(machine_cpu_cores, "instance", "cadvisor", "instance", ".*cadvisor.*")
) * 100
